from bs4 import BeautifulSoup
import re
from typing import List, Optional, Dict, Any
from dataclasses import dataclass, field
import json


@dataclass
class FacebookImage:
    """Structure de données pour une image d'un post Facebook"""
    url: str
    alt: str = ""
    width: int = 0
    height: int = 0


@dataclass
class FacebookPost:
    """Structure de données pour un post Facebook parsé"""
    author: Optional[str] = None
    content: Optional[str] = None
    tags: List[str] = field(default_factory=list)
    images: List[FacebookImage] = field(default_factory=list)
    timestamp: Optional[str] = None
    url: Optional[str] = None
    reactions_count: int = 0
    comments_count: int = 0
    shares_count: int = 0


class FacebookPostParser:
    """Parser avancé pour extraire auteur, contenu, tags et images des posts Facebook"""

    def __init__(self):
        # Sélecteurs CSS pour les éléments essentiels
        self.author_selectors = [
            'h2 a',
            'h3 a[role="link"]',
            '[data-ad-preview="message"] h3 a',
            'h3 span strong a',
            '.actor a',
            '[data-testid="post_author_link"]',
            'a[data-hovercard*="user"]',
            'h2 strong a'
        ]

        self.content_selectors = [
            '[data-ad-comet-preview="message"] div[dir="auto"]',
            '[data-ad-preview="message"] div[dir="auto"]',
            '[data-testid="post_message"]',
            'div[data-ad-preview="message"]',
            '.userContent',
            '[data-testid="post_message"] div[dir="auto"]'
        ]

        self.image_selectors = [
            'img.scaledImageFitWidth',
            'img.scaledImageFitHeight',
            'img[alt]:not([alt=""]):not([height="16"])',
            '.x168nmei',
            'a[href*="photo"] img',
            '[data-ploi] img'
        ]

        self.timestamp_selectors = [
            '.timestamp',
            '.fcg abbr',
            '[data-utime]',
            'span[id*="js"] a',
            '.timestampContent'
        ]

        self.see_more_button_selectors = [
            'div[role="button"]',
            '.see_more_link',
            '.text_exposed_link',
            '.x1i10hfl'
        ]

        self.reaction_count_selectors = [
            'span.x1e558r4',
            '.UFILikeSentence',
            '[data-testid="UFI2ReactionsCount"]'
        ]

        self.comment_count_selectors = [
            'span[dir="auto"]:contains("comment")',
            '.UFICommentLink',
            '[data-testid="UFI2CommentsCount"]'
        ]

        self.share_count_selectors = [
            'span[dir="auto"]:contains("share")',
            '.UFIShareLink',
            '[data-testid="UFI2SharesCount"]'
        ]

    def parse_post(self, html_content: str) -> FacebookPost:
        """
        Parse un post Facebook depuis son HTML

        Args:
            html_content: Contenu HTML du post Facebook

        Returns:
            FacebookPost: Objet contenant auteur, contenu, tags, images, etc.
        """
        soup = BeautifulSoup(html_content, 'html.parser')

        post = FacebookPost()

        # Extraction de l'auteur
        post.author = self._extract_author(soup)

        # Extraction du contenu (avec gestion du "See more")
        post.content = self._extract_full_content(soup)

        # Extraction des tags depuis le contenu
        post.tags = self._extract_tags(post.content)

        # Extraction des images
        post.images = self._extract_images(soup)

        # Extraction de l'horodatage
        post.timestamp = self._extract_timestamp(soup)

        # Extraction de l'URL du post
        post.url = self._extract_post_url(soup)

        # Extraction des compteurs
        post.reactions_count = self._extract_reactions_count(soup)
        post.comments_count = self._extract_comments_count(soup)
        post.shares_count = self._extract_shares_count(soup)

        return post

    def _extract_author(self, soup: BeautifulSoup) -> Optional[str]:
        """Extrait le nom de l'auteur du post"""
        for selector in self.author_selectors:
            element = soup.select_one(selector)
            if element:
                author = element.get_text(strip=True)
                if author:
                    return author
        return None

    def _extract_full_content(self, soup: BeautifulSoup) -> Optional[str]:
        """Extrait le contenu textuel complet du post, y compris après le 'See more'"""
        content = None

        # Tenter d'extraire le contenu avec les sélecteurs standard
        for selector in self.content_selectors:
            elements = soup.select(selector)
            if elements:
                # Prendre tous les éléments correspondants et les joindre
                # Cela aide à récupérer le contenu même s'il est divisé par un bouton "See more"
                full_content = "\n".join([elem.get_text(separator=' ', strip=True) for elem in elements])
                if full_content:
                    content = full_content
                    break

        # Si aucun contenu n'a été trouvé, essayons une approche plus générique
        if not content:
            # Chercher des paragraphes ou des div avec du texte
            potential_content_elements = soup.select('div[dir="auto"], p, div.userContent')
            if potential_content_elements:
                content = "\n".join([elem.get_text(separator=' ', strip=True) for elem in potential_content_elements])

        # Essayer de trouver du contenu caché après un "See more"
        see_more_content = self._extract_hidden_content(soup)
        if see_more_content and content:
            # Si nous trouvons du contenu caché, l'ajouter au contenu principal
            content = f"{content}\n{see_more_content}"

        return self._clean_content(content) if content else None

    def _extract_hidden_content(self, soup: BeautifulSoup) -> Optional[str]:
        """Tente d'extraire le contenu caché derrière un bouton 'See more'"""
        hidden_content = None

        # Rechercher des éléments cachés qui contiennent du texte
        for selector in self.see_more_button_selectors:
            see_more_btn = soup.select_one(selector)
            if see_more_btn:
                # Regarder les éléments parents/voisins qui pourraient contenir le texte complet
                parent = see_more_btn.parent
                if parent:
                    # Essayer de trouver un élément voisin avec plus de texte
                    siblings = list(parent.parent.children) if parent.parent else []
                    for sibling in siblings:
                        if hasattr(sibling, 'get_text') and len(sibling.get_text(strip=True)) > len(
                                parent.get_text(strip=True)):
                            hidden_content = sibling.get_text(strip=True)
                            break

        return hidden_content

    def _extract_tags(self, content: Optional[str]) -> List[str]:
        """Extrait les hashtags et mentions du contenu"""
        if not content:
            return []

        tags = []

        # Extraction des hashtags (#)
        hashtags = re.findall(r'#\w+', content)
        tags.extend(hashtags)

        # Extraction des mentions (@)
        mentions = re.findall(r'@\w+', content)
        tags.extend(mentions)

        # Suppression des doublons
        return list(set(tags))

    def _extract_images(self, soup: BeautifulSoup) -> List[FacebookImage]:
        """Extrait les images du post Facebook"""
        images = []

        for selector in self.image_selectors:
            img_elements = soup.select(selector)
            for img in img_elements:
                # Éviter les petites icônes et émojis
                if 'width' in img.attrs and 'height' in img.attrs:
                    try:
                        width = int(img['width'])
                        height = int(img['height'])
                        if width < 50 or height < 50:  # Ignorer les petites images
                            continue
                    except (ValueError, TypeError):
                        pass

                # Vérifier si c'est une image d'emoji
                if 'emoji.php' in img.get('src', ''):
                    continue

                # Vérifier si c'est un avatar ou une icône
                if any(cls in img.get('class', []) for cls in ['avatar', 'icon', 'emoticon']):
                    continue

                # Récupérer l'URL de l'image
                src = img.get('src')
                if not src:
                    continue

                # Récupérer le texte alternatif
                alt = img.get('alt', '')

                # Récupérer les dimensions si disponibles
                width = int(img.get('width', 0)) if img.get('width', '').isdigit() else 0
                height = int(img.get('height', 0)) if img.get('height', '').isdigit() else 0

                # Créer et ajouter l'objet image
                image = FacebookImage(url=src, alt=alt, width=width, height=height)

                # Éviter les doublons
                if not any(i.url == image.url for i in images):
                    images.append(image)

        return images

    def _extract_timestamp(self, soup: BeautifulSoup) -> Optional[str]:
        """Extrait l'horodatage du post"""
        for selector in self.timestamp_selectors:
            element = soup.select_one(selector)
            if element:
                timestamp = element.get('title') or element.get_text(strip=True)
                if timestamp:
                    return timestamp
        return None

    def _extract_post_url(self, soup: BeautifulSoup) -> Optional[str]:
        """Extrait l'URL du post"""
        link_elements = [
            soup.select_one('a[href*="/posts/"]'),
            soup.select_one('a[href*="/photo.php"]'),
            soup.select_one('a[href*="/permalink.php"]'),
            soup.select_one('.timestamp a')
        ]

        for link in link_elements:
            if link and 'href' in link.attrs:
                return link['href']

        return None

    def _extract_reactions_count(self, soup: BeautifulSoup) -> int:
        """Extrait le nombre de réactions"""
        for selector in self.reaction_count_selectors:
            element = soup.select_one(selector)
            if element:
                text = element.get_text(strip=True)
                # Extraire les chiffres du texte
                numbers = re.findall(r'\d+', text)
                if numbers:
                    return int(numbers[0])
        return 0

    def _extract_comments_count(self, soup: BeautifulSoup) -> int:
        """Extrait le nombre de commentaires"""
        for selector in self.comment_count_selectors:
            elements = soup.select(selector)
            for element in elements:
                text = element.get_text(strip=True)
                if 'comment' in text.lower():
                    numbers = re.findall(r'\d+', text)
                    if numbers:
                        return int(numbers[0])
        return 0

    def _extract_shares_count(self, soup: BeautifulSoup) -> int:
        """Extrait le nombre de partages"""
        for selector in self.share_count_selectors:
            elements = soup.select(selector)
            for element in elements:
                text = element.get_text(strip=True)
                if 'share' in text.lower():
                    numbers = re.findall(r'\d+', text)
                    if numbers:
                        return int(numbers[0])
        return 0

    def _clean_content(self, content: str) -> str:
        """Nettoie le contenu extrait"""
        if not content:
            return ""

        # Supprimer les espaces multiples et normaliser les sauts de ligne
        content = re.sub(r'\n+', '\n', content)
        content = re.sub(r' +', ' ', content)

        # Supprimer les "See more" et autres textes liés à l'interface
        content = re.sub(r'See more|See translation|Read more|Show more', '', content)

        # Supprimer les séquences spéciales qui peuvent rester dans le texte
        content = re.sub(r'[\u200b-\u200f\u2028-\u202f\u205f-\u206f]+', '', content)

        return content.strip()

    def to_json(self, post: FacebookPost) -> str:
        """Convertit un objet FacebookPost en JSON"""
        post_dict = {
            "author": post.author,
            "content": post.content,
            "tags": post.tags,
            "images": [{"url": img.url, "alt": img.alt, "width": img.width, "height": img.height} for img in
                       post.images],
            "timestamp": post.timestamp,
            "url": post.url,
            "stats": {
                "reactions": post.reactions_count,
                "comments": post.comments_count,
                "shares": post.shares_count
            }
        }
        return json.dumps(post_dict, ensure_ascii=False, indent=2)


