from flask import Flask, request, jsonify
from flask_cors import CORS
from moduls.ClaimExtractor import ClaimExtractor
from moduls.Prompt import Prompt
from moduls.ollama import OllamaAPI
app = Flask(__name__)
CORS(app)
ollama_api_url = "http://localhost:11434/api/generate"
MODEL_NAME = "llama3.2:3b"

@app.route("/ask", methods=["POST","GET"])
def ask_ollama():
    try:
        data = request.args.get('html')
        post_id = request.args.get('id', '')
        print(data)
        extractor = ClaimExtractor()
        claims = extractor.extract_claims(data)
        if not claims:
            return jsonify({"message": "No claims found in the post", "claims": []}), 200
        prompt_generator = Prompt(claims)
        prompt = prompt_generator.prompt()
        ollama_api = OllamaAPI()
        response = ollama_api.generate_response(prompt)
        det=""
        for ch in response:
            if ch=="2":
                break
            elif ch=="1":
                pass
            else:
                det=det + ch
        return jsonify({
            "id":post_id,
            "response": det
        })
    except Exception as e:
        app.logger.error(f"Error processing request: {str(e)}")
        return jsonify({"error": str(e)}), 500















